'use strict';
const mul = require('../lib/mul');

test('23456789123456789123456789 * (-1) to equal -23456789123456789123456789', () =>{
  expect(mul('23456789123456789123456789','-1')).toBe('-23456789123456789123456789');
});

test('23456789123456789123456789 * 0 to equal 0', () =>{
  expect(mul('23456789123456789123456789','0')).toBe('0');
});

test('23456789123456789123456789 * 33332 to equal 781861695063061695063061690948', () =>{
  expect(mul('23456789123456789123456789','33332')).toBe('781861695063061695063061690948');
});

test('-23456789123456789123456789 * - 1 to equal 23456789123456789123456788', () =>{
  expect(mul('-23456789123456789123456789','-1')).toBe('23456789123456789123456789');
});

test('-2 * (-2) to equal 4', () =>{
  expect(mul("-2", "-2")).toBe("4");
});

test('-2 * (-2) to equal 4', () =>{
  expect(mul("-2", "-2")).toBe("4");
});

test('33323232 * 0 to equal 0', () =>{
  expect(mul("33323232", "0")).toBe("0");
});

test('0 * 33323232 to equal 0', () =>{
  expect(mul("0", "33323232")).toBe("0");
});

test('2 * (-2) to equal -4', () =>{
  expect(mul("2", "-2")).toBe("-4");
});

test('-5 * 4 to equal 20', () =>{
  expect(mul("-5", "4")).toBe("-20");
});

test('8 * 9 to equal 72', () =>{
  expect(mul("8", "9")).toBe("72");
});

test('-5 * (-6) to equal 30', () =>{
  expect(mul("-5", "-6")).toBe("30");
});

test('2325863608 * 3398 to equal 7903284539984', () =>{
  expect(mul("2325863608", "3398")).toBe("7903284539984");
});

test('2325863608 * 7 to equal 7903284539984', () =>{
  expect(mul("2325863608", "11")).toBe("25584499688");
});

test('2325863608 * 12 to equal 7903284539984', () =>{
  expect(mul("2325863608", "12")).toBe("27910363296");
});

test('2325863608 * 13 to equal 7903284539984', () =>{
  expect(mul("2325863608", "13")).toBe("30236226904");
});

test('2325863608 * 3139527449 to equal 7302112639946175992', () =>{
  expect(mul("2325863608", "3139527449")).toBe("7302112639946175992");
});

test('2325863 3139527 to equal 7302109686801', () =>{
  expect(mul("2325863", "3139527")).toBe("7302109686801");
});